<?class extensions_checks_file extends extensions_checks_db{	function _check_IsInstalldirOk($pagenum, $varnum, $overrideInstalldir= false)	{		$value= $_POST[$this->config['pages'][$pagenum]['data'][$varnum]['varname']];		$return= array ('value' => $value, 'isset' => false);		if ($overrideInstalldir)		{			$value= $overrideInstalldir;		}		if (!is_dir($value))		{			if (@ mkdir($value, 0777))			{				$fh= @ fopen("$value/test", "w+");				if ($fh)				{					fclose($fh);					unlink("$value/test");					$return['isset']= true;				}				rmdir($value);			}		}		return $return;	}}?>